"use client";

import { useAppDispatch, useAppSelector } from "@/lib/hooks";
import { fetchBooks } from "@/store/searchSlice";
import { Button, Image, Table } from "antd";
import type { ColumnsType } from "antd/es/table";

const BookTable = () => {
  const dispatch = useAppDispatch();

  // Accessing Redux state for book data, loading state, current page, and search query
  const { books, total, currentPage, query, loading } = useAppSelector(
    (state) => state.search
  );

  // Define the columns for the Ant Design Table component
  const columns: ColumnsType<any> = [
    {
      title: "Cover",
      dataIndex: "cover_i",
      key: "cover",
      render: (coverId: number) => (
        <Image
          src={`https://covers.openlibrary.org/b/id/${coverId}-M.jpg`}
          fallback="data:image/png;base64,"
          alt="Book cover"
          width={50}
        />
      ),
    },

    {
      title: "Title",
      dataIndex: "title",
      key: "title",
      render: (text) => <p>{text}</p>,
    },
    {
      title: "Author",
      dataIndex: "author_name",
      key: "author",
      render: (authors: string[]) => authors?.join(", "), // Show multiple authors separated by comma
    },
    {
      title: "Link",
      key: "author",
      render: (record) => (
        <Button
          type="dashed"
          href={`/book/${record.key.replace("/works/", "")}`} // Clean up the key for routing
        >
          View Book
        </Button>
      ),
    },
  ];

  return (
    <Table
      rowKey={(record) => record.key}
      columns={columns}
      dataSource={books}
      size="small"
      pagination={{
        current: currentPage,
        total: total,
        pageSize: 10,
        style: { gap: "10px" },
        showSizeChanger: false,
        onChange: (page) => {
          dispatch(fetchBooks({ query, page }));
        },
      }}
      loading={loading}
    />
  );
};

export default BookTable;
